// 대화 목록 전체를 로드하고 검색하는 기능을 처리하는 Content Script

// 팝업으로부터 메시지 수신
chrome.runtime.onMessage.addListener((request, sender, sendResponse) => {
    if (request.action === 'search') {
        loadAllChatHistory(request.term);
    } else if (request.action === 'open_chat') {
        openChat(request.chatId);
    } else if (request.action === 'save_chats') {
        saveChatsToFile();
