            }
            attempts++;
        } while (loadMoreButton && attempts < maxAttempts);

        if (attempts >= maxAttempts) {
            console.warn(`CollectAllChats: 최대 시도 횟수(${maxAttempts}) 도달. 모든 대화가 로드되지 않았을 수 있습니다.`);
        }

        /* --- 동적 로딩 로직 활성화 끝 --- */

