        if (!chatId) {
            console.warn(`CollectAllChats: Chat ID 추출 실패 (${index}번째 링크, href: ${href})`);
            return;
        }
        // 중복 ID 처리 (동적 로딩 중 일시적으로 중복될 수 있음)
        if (processedIds.has(chatId)) {
            // console.log(`CollectAllChats: 중복 ID 건너뜀: ${chatId}`);
            return;
        }

        // 링크 내에서 제목 요소(.conversation-title)를 찾거나, 없으면 링크 자체의 텍스트 사용
        const titleElement = linkElement.querySelector('.conversation-title');
