    // 검색 버튼 클릭 이벤트 리스너 추가
    searchButton.addEventListener('click', performSearch);

    // 엔터 키 이벤트 리스너 추가
    searchInput.addEventListener('keypress', (e) => {
        if (e.key === 'Enter') {
            performSearch();
        }
    });

    // 저장 버튼 이벤트 리스너 추가
    const saveButton = document.getElementById('saveButton');
    saveButton.addEventListener('click', () => {
        // 저장 중임을 표시
        resultsList.innerHTML = '<div style="text-align: center;">대화 목록 저장 중...</div>';

        // 현재 활성화된 탭에서 content script로 저장 요청 전송
        chrome.tabs.query({active: true, currentWindow: true}, (tabs) => {
            if (tabs[0] && tabs[0].url.includes('gemini.google.com')) {
                chrome.tabs.sendMessage(tabs[0].id, {
                    action: 'save_chats'
                });
            } else {
                resultsList.innerHTML = '<div style="color: red;">Gemini 웹사이트에서만 사용할 수 있습니다.</div>';
            }
        });
    });

    function performSearch() {
        const searchTerm = searchInput.value.trim();
        if (!searchTerm) return;

        // 검색 중임을 표시
        resultsList.innerHTML = '<div style="text-align: center;">검색 중...</div>';

        // 현재 활성화된 탭에서 content script로 검색 요청 전송
        chrome.tabs.query({active: true, currentWindow: true}, (tabs) => {
            if (tabs[0] && tabs[0].url.includes('gemini.google.com')) {
                chrome.tabs.sendMessage(tabs[0].id, {
                    action: 'search',
                    term: searchTerm
                });
            } else {
                resultsList.innerHTML = '<div style="color: red;">Gemini 웹사이트에서만 사용할 수 있습니다.</div>';
            }
        });
    }

    // 검색 결과 표시 메시지 리스너
    chrome.runtime.onMessage.addListener((message) => {
        if (message.action === 'search_results') { // 'display_results' -> 'search_results'로 수정
            displayResults(message.results);
        } else if (message.action === 'save_complete') {
            if (message.success) {
                resultsList.innerHTML = '<div style="color: green;">대화 목록이 성공적으로 저장되었습니다.</div>';
            } else {
                resultsList.innerHTML = `<div style="color: red;">저장 중 오류가 발생했습니다: ${message.error || '알 수 없는 오류'}</div>`;
            }
        }
    });

    function displayResults(results) {
        resultsList.innerHTML = '';

        if (results.length === 0) {
            resultsList.innerHTML = '<div style="text-align: center;">검색 결과가 없습니다.</div>';
            return;
        }

        results.forEach(result => {
            const resultItem = document.createElement('div');
            resultItem.className = 'result-item';
            resultItem.textContent = result.title;
            resultItem.addEventListener('click', () => {
                // 선택한 대화로 이동 요청
                chrome.tabs.query({active: true, currentWindow: true}, (tabs) => {
                    chrome.tabs.sendMessage(tabs[0].id, {
                        action: 'open_chat',
                        chatId: result.chatId
                    });
                });
                // 팝업 닫기
                window.close();
            });
            resultsList.appendChild(resultItem);
        });
    }
});

